create table if not exists Customer(custName text NOT NULL,	abbreviation text NOT NULL,	PPA text NOT NULL, primary key (custName), check (PPA = 'Y' OR PPA = 'N'));
create table if not exists Site(custName text NOT NULL,	siteName text NOT NULL,	streetAddress text NOT NULL, stateAddress text,	cityAddress text NOT NULL, countryAddress text NOT NULL, zipAddress text, primary key (custName, siteName),	foreign key (custName) references Customer(custName) on delete cascade);
create table if not exists AccountNumber(custName text NOT NULL, siteName text NOT NULL, serviceName text NOT NULL,	accountNum text NOT NULL, billingZip text NOT NULL,	primary key (custName, siteName, serviceName), foreign key (custName, siteName) references Site(custName, siteName) on delete cascade);